document.body.addEventListener("click",function(e){var target=e.target;if(target.tagName==="INPUT"){target.select()}});$(document).ready(function(){var search=$("#search");var icons=$('i[class^="kik-"]');var iconBlock=$('div[class*="glyph"]');var iconCount=$("#iconCount");iconCount.text(iconBlock.length);search.val("");search.keyup(function(){icons.css("color","#555");search.css("box-shadow","inset 0 -2px #449fdb");iconBlock.css("display","none");search.removeClass("init-shake");var name=$("#search").val();if(name!==""){var elements=$('i[class*="'+name+'"]');elements.css("color","crimson");elements.parent().parent().css("display","block");iconCount.text(elements.length);if(elements.length===0){search.css("box-shadow","inset 0 -2px crimson");search.addClass("init-shake")}}else{icons.css("color","#555");iconBlock.css("display","block");iconCount.text(iconBlock.length)}})});